mapper.select:
select dayoff_no, dayoff_day, days, oneorhalf
from dayoff_apply_detail
where dayoff_no = #{dayoff_no}

mapper.update:
update
dayoff_create_terms set dayoff_days = #{dayoff_days} where
year_in_office = #{year_in_office}

mapper.select:
select count(*) from dayoff_apply_detail dad join dayoff_apply da on dad.dayoff_no = da.dayoff_no join document d on da.document_no = d.document_no where employee_no = #{employee.employee_no} and to_char(dayoff_day,'yyyy/mm/dd') in
#{item}
and document_state != '반려'

select da.dayoff_no, e.employee_name, dayoff_name, da.total_days, start_date, end_date, document_state from dayoff_apply da join document d on da.document_no = d.document_no join employee e on d.document_writer_no = e.employee_no join dayoff_kinds dk on da.dayoff_type_code = dk.dayoff_type_code
da.employee_no = #{employee.employee_no}
and da.dayoff_type_code = #{dayoff_kind.dayoff_type_code}
and to_char(document_date,'yyyy') = to_char(#{document.document_date},'yyyy')
order by document_date desc

select dayoff_no, dayoff_day, days, oneorhalf
from dayoff_apply_detail
where dayoff_no = #{dayoff_no}
